import React, { Component } from 'react';
import PropTypes from 'prop-types';
import withStyles from '@material-ui/core/styles/withStyles';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import ListSubheader from '@material-ui/core/ListSubheader';
import Divider from '@material-ui/core/Divider';

import Icon from '@material-ui/core/Icon';

const styles = theme => ({
    root: {
        width: '90%',
    },
    // button: {
    //   marginTop: theme.spacing.unit,
    //   marginRight: theme.spacing.unit,
    // },
    // actionsContainer: {
    //   marginBottom: theme.spacing.unit * 2,
    // },
    // resetContainer: {
    //   padding: theme.spacing.unit * 3,
    // },
    // form: {
    //   width: '100%', // Fix IE 11 issue.
    //   marginTop: theme.spacing.unit,
    // },
    // textField: {
    //   marginLeft: theme.spacing.unit,
    //   marginRight: theme.spacing.unit,
    //   width: 200,
    // },

});


class NavigatorListItems extends Component {
    constructor(props) {
        super(props);
        this.state = {
            listItemLabel: {
                taskManage: '任务管理',
                securityConfig: '安全配置管理',
                inspectResult: '检测结果',
                riskWarning: '风险预警',
                accountInfo: '账号信息',
                signupManage: '注册管理',
                logout: '退出账号',
            },
            listItemIcon: {
                taskManage: "star",
                securityConfig: "home",
                inspectResult: "language",
                riskWarning: "list",
                accountInfo: 'star',
                signupManage: 'home',
                logout: 'language',
            },
        };
    }

    getModuleComponent(name) {
        const {listItemIcon, listItemLabel} = this.state;
        return (
            <ListItem button>
                <ListItemIcon>
                    <Icon>{listItemIcon[name]}</Icon>
                </ListItemIcon>
                <ListItemText primary={listItemLabel[name]} />
            </ListItem>
        );
    };

    render() {
        const { classes } = this.props;
        const {listItemIcon, listItemLabel} = this.state;
        return (
            <div>
                {this.getModuleComponent('taskManage')}
                {this.getModuleComponent('securityConfig')}
                {this.getModuleComponent('inspectResult')}
                {this.getModuleComponent('riskWarning')}
                <Divider />
                {this.getModuleComponent('accountInfo')}
                {this.getModuleComponent('signupManage')}
                {this.getModuleComponent('logout')}
            </div>
        );

    }
}

NavigatorListItems.propTypes = {
    classes: PropTypes.object.isRequired,
};

NavigatorListItems.contextTypes = {
    router: PropTypes.object.isRequired
};

export default withStyles(styles)(NavigatorListItems);
