import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Stepper from '@material-ui/core/Stepper';
import Step from '@material-ui/core/Step';
import StepLabel from '@material-ui/core/StepLabel';
import StepContent from '@material-ui/core/StepContent';
import Button from '@material-ui/core/Button';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import FormControl from '@material-ui/core/FormControl';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Input from '@material-ui/core/Input';
import InputLabel from '@material-ui/core/InputLabel';
import TextField from '@material-ui/core/TextField';

const styles = theme => ({
  root: {
    width: '90%',
  },
  button: {
    marginTop: theme.spacing.unit,
    marginRight: theme.spacing.unit,
  },
  actionsContainer: {
    marginBottom: theme.spacing.unit * 2,
  },
  resetContainer: {
    padding: theme.spacing.unit * 3,
  },
  form: {
    width: '100%', // Fix IE 11 issue.
    marginTop: theme.spacing.unit,
  },
  textField: {
    marginLeft: theme.spacing.unit,
    marginRight: theme.spacing.unit,
    width: 200,
  },

});

// function getSteps() {
//   return ['基本信息', '资产识别', '规则配置'];
// }

// function getStepContent(step) {
//   // const { classes } = this.props;
//   switch (step) {
//     case 0:
//       return (
//         <div>
//           <form>
//             <TextField
//               required
//               id="task-name"
//               label="任务名称"
//               defaultValue="新建任务-1"
//               className={classes.textField}
//               margin="normal"
//             />
//             <FormControl margin="normal" required fullWidth>
//               <InputLabel>任务名称</InputLabel>
//               <Input id="task-name" name="task-name" autoFocus />
//             </FormControl>
//             <FormControl margin="normal" required fullWidth>
//               <InputLabel>任务描述</InputLabel>
//               <Input id="task-desc" name="task-desc" />
//             </FormControl>
//             {/* <FormControl margin="normal" required fullWidth>
//               <InputLabel htmlFor="password">密码</InputLabel>
//               <Input name="password" type="password" id="password" autoComplete="current-password" />
//             </FormControl> */}
//           </form>
//           {/* <form className={classes.form}>
//             <FormControl margin="normal" required fullWidth>
//               <InputLabel htmlFor="email">用户名</InputLabel>
//               <Input id="email" name="email" autoComplete="email" autoFocus  />
//             </FormControl>
//             <FormControl margin="normal" required fullWidth>
//               <InputLabel htmlFor="password">密码</InputLabel>
//               <Input name="password" type="password" id="password" autoComplete="current-password" />
//             </FormControl>
//           </form> */}

//         </div>
//       );
//     case 1:
//       return 'An ad group contains one or more ads which target a shared set of keywords.';
//     case 2:
//       return `Try out different ad text to see what brings in the most customers,
//               and learn how to enhance your ads using features like ad extensions.
//               If you run into any problems with your ads, find out how to tell if
//               they're running and how to resolve approval issues.`;
//     default:
//       return 'Unknown step';
//   }
// }

class NewTaskDialog extends React.Component {
  state = {
    activeStep: 0,
  };

  getSteps = () => {
    return ['基本信息', '资产识别', '规则配置'];
  };
  
  getStepContent = (step) => {
    const { classes } = this.props;
    switch (step) {
      case 0:
        return (
          <div>
            <form>
              {/* <TextField required id="task-name" label="任务名称" defaultValue="新建任务-1"
                className={classes.textField} margin="normal" fullWidth autoFocus /> */}
              <FormControl margin="normal" required fullWidth>
                <InputLabel>任务名称</InputLabel>
                <Input id="task-name1" name="task-name1" autoFocus />
              </FormControl>
              <FormControl margin="normal" required fullWidth>
                <InputLabel>任务描述</InputLabel>
                <Input id="task-desc" name="task-desc" />
              </FormControl>
              {/* <FormControl margin="normal" required fullWidth>
                <InputLabel htmlFor="password">密码</InputLabel>
                <Input name="password" type="password" id="password" autoComplete="current-password" />
              </FormControl> */}
            </form>
            {/* <form className={classes.form}>
              <FormControl margin="normal" required fullWidth>
                <InputLabel htmlFor="email">用户名</InputLabel>
                <Input id="email" name="email" autoComplete="email" autoFocus  />
              </FormControl>
              <FormControl margin="normal" required fullWidth>
                <InputLabel htmlFor="password">密码</InputLabel>
                <Input name="password" type="password" id="password" autoComplete="current-password" />
              </FormControl>
            </form> */}
  
          </div>
        );
      case 1:
        return 'An ad group contains one or more ads which target a shared set of keywords.';
      case 2:
        return `Try out different ad text to see what brings in the most customers,
                and learn how to enhance your ads using features like ad extensions.
                If you run into any problems with your ads, find out how to tell if
                they're running and how to resolve approval issues.`;
      default:
        return 'Unknown step';
    }
  };
  
  handleNext = () => {
    this.setState(state => ({
      activeStep: state.activeStep + 1,
    }));
  };

  handleBack = () => {
    this.setState(state => ({
      activeStep: state.activeStep - 1,
    }));
  };

  handleReset = () => {
    this.setState({
      activeStep: 0,
    });
  };

  render() {
    const { classes } = this.props;
    const steps = this.getSteps();
    const { activeStep } = this.state;

    return (
      <div className={classes.root}>
        <Stepper activeStep={activeStep} orientation="vertical">
          {steps.map((label, index) => (
            <Step key={label}>
              <StepLabel>{label}</StepLabel>
              <StepContent>
                <Typography>{this.getStepContent(index)}</Typography>
                <div className={classes.actionsContainer}>
                  <div>
                    <Button
                      disabled={activeStep === 0}
                      onClick={this.handleBack}
                      className={classes.button}
                    >
                      Back
                    </Button>
                    <Button
                      variant="contained"
                      color="primary"
                      onClick={this.handleNext}
                      className={classes.button}
                    >
                      {activeStep === steps.length - 1 ? 'Finish' : 'Next'}
                    </Button>
                  </div>
                </div>
              </StepContent>
            </Step>
          ))}
        </Stepper>
        {activeStep === steps.length && (
          <Paper square elevation={0} className={classes.resetContainer}>
            <Typography>All steps completed - you&apos;re finished</Typography>
            <Button onClick={this.handleReset} className={classes.button}>
              Reset
            </Button>
          </Paper>
        )}
      </div>
    );
  }
}

NewTaskDialog.propTypes = {
  classes: PropTypes.object,
};

export default withStyles(styles)(NewTaskDialog);
