const IsUserLogin = () => {
    const {user, password, login, expire} = LoadUserLoginInfo();
    return typeof (login) === 'object' && login === true;
};

const UserLogin = (user, password, rememberDays) => {
    let expireDate = new Date();
    if (rememberDays > 0) {
        expireDate.setTime(expireDate.getTime() + rememberDays * 24 * 60 * 60 * 1000);
    } else {
        expireDate.setTime(expireDate.getTime() - 1);
    }

    document.cookie = {
        user: user,
        password: password,
        login: true,
        expire: expireDate,
    };
};

const UserLogout = () => {
    const {user, password, expire} = LoadUserLoginInfo();
    document.cookie = {
        user: user,
        password: password,
        login: false,
        expire: expire,
    };
}

const LoadUserLoginInfo = () => {
    let loginInfo = document.cookie;
    return loginInfo;
};


export { UserLogin, LoadUserLoginInfo, IsUserLogin, UserLogout };
