import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Stepper from '@material-ui/core/Stepper';
import Step from '@material-ui/core/Step';
import StepLabel from '@material-ui/core/StepLabel';
import StepContent from '@material-ui/core/StepContent';
import Button from '@material-ui/core/Button';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import TextField from '@material-ui/core/TextField';

const styles = theme => ({
  root: {
    width: '90%',
  },
  button: {
    marginTop: theme.spacing.unit,
    marginRight: theme.spacing.unit,
  },
  actionsContainer: {
    marginBottom: theme.spacing.unit * 2,
  },
  resetContainer: {
    padding: theme.spacing.unit * 3,
  },
  form: {
    width: '100%', // Fix IE 11 issue.
    marginTop: theme.spacing.unit,
  },
  textField: {
    marginLeft: theme.spacing.unit,
    marginRight: theme.spacing.unit,
    width: 200,
  },

});

class NewTaskDialog extends React.Component {
  state = {
    activeStep: 0,
    taskName: '新建任务-1',
    taskDesc: '',
  };

  getSteps = () => {
    return ['基本信息', '资产识别', '规则配置'];
  };

  getStepContent = (step) => {
    const { classes } = this.props;
    const { taskName, taskDesc } = this.state;
    switch (step) {
      case 0:
        return (
          <div>
            <form>
              <TextField required fullWidth autoFocus style={{ margin: 8 }} margin="normal" 
                id="task-name" label="任务名称" defaultValue={taskName} variant="outlined"
                onChange={this.handleTaskNameChange.bind(this)}
              />
              <TextField fullWidth style={{ margin: 8 }} margin="normal" multiline
                id="task-desc" label="任务描述" defaultValue={taskDesc} variant="outlined" rows="4" 
                onChange={this.handleTaskDescChange}
              />
            </form>
          </div>
        );
      case 1:
        return (
          <div>
            <form>
              <TextField required fullWidth autoFocus id="host-name" label="主机名称" defaultValue={""}
                variant="outlined"
              />
              <TextField required fullWidth id="host-ip" label="主机IP" defaultValue={""}
                variant="outlined"
              />
              <TextField required fullWidth id="login-user" label="用户名称" defaultValue={""}
                variant="outlined"
              />
              <TextField required fullWidth id="login-pwd" label="登录密码" defaultValue={""}
                variant="outlined"
              />

            </form>
          </div>
        );
      case 2:
        return `Try out different ad text to see what brings in the most customers,
                and learn how to enhance your ads using features like ad extensions.
                If you run into any problems with your ads, find out how to tell if
                they're running and how to resolve approval issues.`;
      default:
        return 'Unknown step';
    }
  };

  handleNext = () => {
    this.setState(state => ({
      activeStep: state.activeStep + 1,
    }));
  };

  handleBack = () => {
    this.setState(state => ({
      activeStep: state.activeStep - 1,
    }));
  };

  handleReset = () => {
    this.setState({
      activeStep: 0,
    });
  };

  handleTaskNameChange = (event) => {
    this.setState({
      taskName: event.target.value,
    });
  };

  handleTaskDescChange = (event) => {
    this.setState({
      taskDesc: event.target.value,
    });
  };

  render() {
    const { classes } = this.props;
    const steps = this.getSteps();
    const { activeStep } = this.state;

    return (
      <div className={classes.root}>
        <Stepper activeStep={activeStep} orientation="vertical">
          {steps.map((label, index) => (
            <Step key={label}>
              <StepLabel>{label}</StepLabel>
              <StepContent>
                <Typography>{this.getStepContent(index)}</Typography>
                <div className={classes.actionsContainer}>
                  <div>
                    <Button
                      disabled={activeStep === 0}
                      onClick={this.handleBack}
                      className={classes.button}
                    >
                      上一步
                    </Button>
                    <Button
                      variant="contained"
                      color="primary"
                      onClick={this.handleNext}
                      className={classes.button}
                    >
                      {activeStep === steps.length - 1 ? '完成' : '下一步'}
                    </Button>
                  </div>
                </div>
              </StepContent>
            </Step>
          ))}
        </Stepper>
        {activeStep === steps.length && (
          <Paper square elevation={0} className={classes.resetContainer}>
            <Typography>All steps completed - you&apos;re finished</Typography>
            <Button onClick={this.handleReset} className={classes.button}>
              Reset
            </Button>
          </Paper>
        )}
      </div>
    );
  }
}

NewTaskDialog.propTypes = {
  classes: PropTypes.object,
};

export default withStyles(styles)(NewTaskDialog);
