import React, { Component } from 'react';
import withStyles from '@material-ui/core/styles/withStyles';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import ListSubheader from '@material-ui/core/ListSubheader';

import DashboardIcon from '@material-ui/icons/Dashboard';
import ShoppingCartIcon from '@material-ui/icons/ShoppingCart';
import PeopleIcon from '@material-ui/icons/People';
import BarChartIcon from '@material-ui/icons/BarChart';
import LayersIcon from '@material-ui/icons/Layers';
import AssignmentIcon from '@material-ui/icons/Assignment';

const styles = theme => ({
    root: {
        width: '90%',
    },
    // button: {
    //   marginTop: theme.spacing.unit,
    //   marginRight: theme.spacing.unit,
    // },
    // actionsContainer: {
    //   marginBottom: theme.spacing.unit * 2,
    // },
    // resetContainer: {
    //   padding: theme.spacing.unit * 3,
    // },
    // form: {
    //   width: '100%', // Fix IE 11 issue.
    //   marginTop: theme.spacing.unit,
    // },
    // textField: {
    //   marginLeft: theme.spacing.unit,
    //   marginRight: theme.spacing.unit,
    //   width: 200,
    // },

});


class TaskListItems extends Component {
    constructor(props) {
        super(props);
        this.state = {
            listItemLabel: {
                taskManage: '任务管理',
                securityConfig: '安全配置管理',
                inspectResult: '检测结果',
                riskWarning: '风险预警',
            },
            listItemIcon: {
                taskManage: DashboardIcon,
                securityConfig: ShoppingCartIcon,
                inspectResult: PeopleIcon,
                riskWarning: BarChartIcon,
            },
        };
    }

    getModuleComponent(name) {
        const {listItemIcon, listItemLabel} = this.state;
        return (
            <ListItem button>
                <ListItemIcon>
                    <div>{listItemIcon[name]}</div>
                </ListItemIcon>
                <ListItemText primary={listItemLabel[name]} />
            </ListItem>
        );
    };

    render() {
        const { classes } = this.props;
        return (
            <List>
                {getModuleComponent('taskManage')}
                {getModuleComponent('securityConfig')}
                {getModuleComponent('inspectResult')}
                {getModuleComponent('riskWarning')}
            </List>
        );

    }
}

TaskListItems.propTypes = {
    classes: PropTypes.object.isRequired,
};

TaskListItems.contextTypes = {
    router: PropTypes.object.isRequired
};

export default withStyles(styles)(TaskListItems);
